mapper.select:
/* 악보 상세 이미지 조회 : SheetMapper.getListSheetImage */
SELECT
    T1.*
FROM SHEET_IMAGE T1
WHERE T1.DELETE_YN = 'N'
    AND T1.SHEET_ID = #{sheetId}

/* 연관 악보 조회 : SheetMapper.getListSheetRecommend */

ORDER BY RAND()
LIMIT 10

mapper.sql:
SELECT T1.* , T2.IMAGE_PATH FROM SHEET T1 LEFT JOIN SHEET_IMAGE T2 ON T1.SHEET_ID = T2.SHEET_ID AND T2.IMAGE_MAIN = 'Y' WHERE T1.DELETE_YN = 'N' AND T2.IMAGE_PATH IS NOT NULL
AND T1.CATEGORY_CODE = #{categoryCode}
AND T1.DISPLAY_TYPE LIKE CONCAT('%',#{displayType},'%')
#{id}
